import { Transform } from 'class-transformer';
export const TransformDate = () => {
  const toISOFormat = params => {
    params.value.setHours(params.value.getHours() + 3);
    return params.value ? params.value.toISOString() : null;
  };
  const toPlain = Transform(toISOFormat, {
    toPlainOnly: true
  });
  const toDate = params => params.value ? new Date(params.value) : null;
  const toClass = Transform(toDate, {
    toClassOnly: true
  });
  return (target, key) => {
    toPlain(target, key);
    toClass(target, key);
  };
};
