import { combineLatest, from } from 'rxjs';
import { map } from 'rxjs/operators';
export const combineLatestIntoObject = sources => {
  const toEntry = key => {
    return value => [key, value];
  };
  const toObject = entries => {
    return Object.fromEntries(entries);
  };
  const toEntryObservable = ([key, value]) => {
    return from(value).pipe(map(toEntry(key)));
  };
  const entries = Object.entries(sources);
  return combineLatest(entries.map(toEntryObservable)).pipe(map(toObject));
};
